from os import makedirs, path, mkdir

app = input("Application name (default app): ")

def HTMLMaker (app_name):
  title = " ".join(app_name.split("-")).title()
  app_folders = ["assets", "assets/css", "assets/js", "assets/images"]
  app_files = [
    f"index.html",
    f"{app_folders[1]}/index.css",
    f"{app_folders[2]}/index.js"
  ]
  favicon = f"{app_folders[3]}/favicon.png"
  app_files_content = [f'''<!DOCTYPE html>
<html lang="en">
  <head>
    <meta charset="UTF-8">
    <meta http-equiv="X-UA-Compatible" content="ie=edge">
    <meta name="viewport" content="width=device-width, initial-scale=1">
    <meta name="keywords" content="{title}">
    <meta name="description" content="Welcome at {title}">
    <meta name="author" content="WEB Developer">
    <link rel="icon" href="{favicon}" type="image/png" sizes="32x32">
    <title>{title}</title>
    <link rel="stylesheet" href="{app_files[1]}">
  </head>
  <body>

    <script src="{app_files[2]}"></script>
  </body>
</html>''',
  '''* {
  background-color: transparent;
  border: none;
  margin: 0;
  padding: 0;
}
*, ::after, ::before {
  box-sizing: border-box;
}
[type="button"], [type="reset"], [type="submit"], button, select {
  cursor: pointer;
  user-select: none;
}
button, datalist, input, select, textarea {
  color: initial;
  font-family: inherit;
}
article, aside, datalist, footer, header, img, main, nav, section, svg, video {
  display: block;
}
code, kbd, pre, samp, var {
  font-family: monospace;
}''',
  '''(function () {
  
})();'''
  ]
  if (not path.exists(app_name)):
    mkdir(app_name)
  for folder in app_folders:
    if (not path.exists(f"{app_name}/{folder}")):
      mkdir(f"{app_name}/{folder}")
  for app_file in app_files:
    with open(f"{app_name}/{app_file}", "w") as file:
      file.write(app_files_content[app_files.index(app_file)])
